import React from "react";
import Modal from "@mui/material/Modal";
import { Box } from "@mui/material";
import { Link } from "react-router-dom";

const style = {
  position: "absolute" as "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  bgcolor: "background.paper",
  boxShadow: 24,
  p: 4,
};

const Navbar = () => {
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  return (
    <div className="flex justify-between px-8 py-2 h-15 border-b-4">
      <div className="flex gap-5 justify-center items-center">
        <img
          className="w-10 h-10"
          src="data:image/png;base64,"
        />
        <div className="font-bold">NOTICEBOARD</div>
      </div>
      <div className="flex gap-4 justify-center items-center">
        <button onClick={handleOpen}>
          <img
            className="w-5"
            src="https://www.freepnglogos.com/uploads/plus-icon/plus-icon-plus-svg-png-icon-download-1.png"
          />
        </button>
        <img
          className="w-10 rounded-3xl"
          src="https://icon-library.com/images/default-profile-icon/default-profile-icon-24.jpg"
        />
      </div>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <div className="flex flex-col gap-3">
            <p>
              <strong>Join a class:</strong>
              <br />- Click below to join the class.
            </p>
            <button
              onClick={handleClose}
              className="bg-blue-500 text-white rounded-lg hover:bg-white hover:text-black"
            >
              <Link to="/join">Join a class</Link>
            </button>
            <p>
              <strong>Create a class:</strong>
              <br />- Click below to create a class.
            </p>
            <button
              onClick={handleClose}
              className="bg-blue-500 text-white rounded-lg hover:bg-white hover:text-black"
            >
              <Link to="/create">Create a class</Link>
            </button>
          </div>
        </Box>
      </Modal>
    </div>
  );
};

export default Navbar;
